targets.rawSql:
SELECT 
  LatencyTestMeasurments.Index AS "time", 
  LatencyTestMeasurments.Latency AS "Latency - Software Measurment",
  LatencyTestMeasurments.Jitter AS "Jitter - Software Measurment"
FROM LatencyTestResults, LatencyTestMeasurments, LatencyTestParameters   
WHERE LatencyTestMeasurments.LatencyTestResultId = ${Test_Id:singlequote}
AND LatencyTestParameters.LatencyTestResultId = ${Test_Id:singlequote}
AND LatencyTestResults.Id = ${Test_Id:singlequote}
AND LatencyTestMeasurments.Index < ${__to:date:ms}
ORDER BY LatencyTestMeasurments.SendTime

SELECT 
  LatencyTestMeasurments.Index AS "time", 
  LatencyTestMeasurments.CapturedLatency AS "Latency - Hardware Capturing",
  LatencyTestMeasurments.CapturedJitter AS "Jitter - Hardware Capturing"
FROM LatencyTestResults, LatencyTestMeasurments, LatencyTestParameters   
WHERE LatencyTestMeasurments.LatencyTestResultId = ${Test_Id:singlequote}
AND LatencyTestParameters.LatencyTestResultId = ${Test_Id:singlequote}
AND LatencyTestResults.Id = ${Test_Id:singlequote}
AND CapturedLatency <> -1
AND LatencyTestMeasurments.Index < ${__to:date:ms}
ORDER BY LatencyTestMeasurments.SendTime
